import moment from 'moment';
import React from 'react';
import { View, Text, Image, Pressable } from 'react-native';
import { TouchableWithoutFeedback } from 'react-native-gesture-handler';
import { ChatRoom } from '../../types';
import { useNavigation } from '@react-navigation/native';

import styles from './style';

export type ChatListItemProps = {
    chatRoom: ChatRoom;
}

const ChatListItem = ( props: ChatListItemProps ) => {
    
    const { chatRoom } = props;

    const navigation = useNavigation();

    const user = chatRoom.chatRoomUsers.items[0].user;

    const message = chatRoom.lastMessage.content;
    const time = chatRoom.lastMessage.createdAt;
    
    const onClick = () => {
        navigation.navigate('ChatRoom', { 
            id: chatRoom.id,
            name: user.name,
        });
        //console.warn(`clicked on ${user.name}`);
    }

    return (
        <TouchableWithoutFeedback
            onPress={onClick} 
        >
            <View style={styles.container}>
                <View style={styles.leftContainer}>
                    <Image 
                        source={{ uri: user.imageUri }} 
                        style={styles.avatar} 
                    />
                    <View style={styles.midContainer}>
                        <Text style={styles.username}>{user.name}</Text>
                        <Text numberOfLines={1} style={styles.lastMessage}>{message  ? message.length>20 ? message.slice(0,17) + "..." : message : ""}</Text>
                    </View>
                </View>
                
                <Text style={styles.time}>
                    {chatRoom.lastMessage && moment(time).format('DD/MM/YYYY')}
                </Text>
            </View>
        </TouchableWithoutFeedback>
    )
};

export default ChatListItem;
